var urlComp = purl(document.referrer);
var tourName = urlComp.param('tour');
var multipage = urlComp.param('multipage');

String.prototype.capitalize = function() {
    return this.charAt(0).toUpperCase() + this.slice(1);
}

function topBar(message) {
    $("html, body").animate({ scrollTop: 0 }, "slow");
    $("<div />", { "class": 'topbar', text: message }).hide().prependTo("body").slideDown('fast').delay(5000).slideUp(function() { 
        $(this).remove(); 
    });
}

function propertyModel(name, property){
    this.property = property;

    this.propertyName = ko.observable(name.capitalize()+":");
    this.propertyValue = property;
    this.propertyPlaceHolder = ko.computed(function(){
        return "Enter the value of "+this.propertyName();
    }, this);
}

function ViewModel(uri){
    var self = this;
    //$cjs.ko API will bind all properties of your concept to ko observable automatically
    this.cvm = $cjs.ko(uri);

    this.properties = ko.observableArray();

    //loop through available concept properties and create propertyModel for html bindings
    _.each(this.cvm.getConc().propNames(), function(x){
        self.properties.push(new propertyModel(x, self.cvm[x]))
    });

    //handle save action in the form and use $cjs.session API to save the changes to cloud
    this.save = function(){
        $cjs.session.save();
        topBar("Your changes have been successfully saved!");
    }
}

$(function(){
    //The code below is related to the ConceptJS tour and can be ignored.
    if(tourName == "create" && multipage == 2){
        var intro = introJs();
        intro.setOptions({
            steps: [
              	{
              		step: 8,
                	element: document.querySelector('#propertyForm'),
                	intro: "You can now enter values for the properties and click <b>Save changes</b> button to save your changes.",
                	position: "bottom"
              	}
            ],
            showBullets: false
        });
        intro.start().oncomplete(function() {
          	$('#cjsCreateTourDialogDone').modal('show') ;
        });
    }
})
